import React, { useState } from 'react';
import axios from 'axios';

const UploadItem = () => {
  const [form, setForm] = useState({ title: '', description: '', image: null });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm({ ...form, [name]: files ? files[0] : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', form.title);
    formData.append('description', form.description);
    formData.append('image', form.image);

    try {
      const response = await axios.post('http://localhost:5001/api/items', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data);
      setMessage('Item uploaded successfully');
    } catch (error) {
      console.error('Error uploading item:', error.response ? error.response.data : error.message);
      setMessage('Error uploading item');
    }
  };

  return (
    <div className="container">
      <h2>Upload Item</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={form.title} onChange={handleChange} />
        </label>
        <label>
          Description:
          <textarea name="description" value={form.description} onChange={handleChange}></textarea>
        </label>
        <label>
          Image:
          <input type="file" name="image" onChange={handleChange} />
        </label>
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UploadItem;
